CREATE OR REPLACE VIEW rv_pp_order AS SELECT o.ad_client_id, o.ad_org_id, o.isactive, o.created, o.createdby, o.updated, o.updatedby, o.pp_order_id, o.documentno, o.docstatus, o.m_warehouse_id, o.m_product_id, o.qtyentered, o.qtyreject, o.qtyscrap, o.qtybatchs, o.qtybatchsize, o.dateordered, o.datepromised, o.datestart, o.datestartschedule, o.datefinish, o.datefinishschedule, o.dateconfirm, o.datedelivered, o.lot, o.pp_product_bom_id, o.ad_workflow_id, ( SELECT p.weight FROM m_product p WHERE p.m_product_id = o.m_product_id) AS weight, o.c_doctypetarget_id, o.m_attributesetinstance_id, o.planner_id, o.priorityrule, o.ad_orgtrx_id, o.user1_id, o.user2_id, o.c_doctype_id, o.line, o.description, o.s_resource_id, o.floatbefored, o.floatafter, o.c_uom_id, o.qtyordered, o.qtydelivered, o.yield, o.c_project_id, o.c_campaign_id, o.c_activity_id, o.isapproved, o.isprinted, o.isselected, o.processed, o.assay, o.isqtypercentage, o.ordertype, o.issotrx, o.scheduletype, o.serno FROM pp_order o;
;

CREATE OR REPLACE VIEW rv_pp_order_bomline AS SELECT obl.ad_client_id, obl.ad_org_id, obl.createdby, obl.updatedby, obl.updated, obl.created, obl.isactive, obl.pp_order_bom_id, obl.pp_order_bomline_id, obl.pp_order_id, obl.iscritical, obl.componenttype, obl.m_product_id, obl.c_uom_id, round(obl.qtyrequiered, 4) AS qtyrequiered, round(bomqtyreserved(obl.m_product_id, obl.m_warehouse_id, 0), 4) AS qtyreserved, round(bomqtyavailable(obl.m_product_id, obl.m_warehouse_id, 0), 4) AS qtyavailable, round(bomqtyonhand(obl.m_product_id, obl.m_warehouse_id, 0), 4) AS qtyonhand, obl.m_warehouse_id, round(obl.qtybom, 4) AS qtybom, obl.isqtypercentage, round(obl.qtybatch, 4) AS qtybatch, CASE WHEN o.qtybatchs = 0 THEN 1 ELSE round(obl.qtyrequiered / o.qtybatchs, 4) END AS qtybatchsize FROM pp_order_bomline obl JOIN pp_order o ON o.pp_order_id = obl.pp_order_id;
;

CREATE OR REPLACE VIEW rv_pp_operation_activity AS SELECT n.ad_client_id, n.ad_org_id, n.created, n.createdby, n.isactive, n.updated, n.updatedby, n.pp_order_id, n.docstatus, n.value, n.s_resource_id, n.durationrequiered, n.durationreal, n.durationrequiered - n.durationreal AS duration, n.qtydelivered, n.qtyreject, n.qtyscrap, n.datestartschedule, n.datefinishschedule FROM pp_order_node n;
;

CREATE OR REPLACE VIEW rv_pp_order_storage AS SELECT obl.ad_client_id, obl.ad_org_id, obl.createdby, obl.updatedby, obl.updated, obl.created, obl.isactive, obl.pp_order_bom_id, obl.pp_order_bomline_id, obl.pp_order_id, obl.iscritical, obl.m_product_id, ( SELECT p.name FROM m_product p WHERE p.m_product_id = o.m_product_id) AS name, obl.c_uom_id, s.qtyonhand, round(obl.qtyrequiered, 4) AS qtyrequiered, CASE WHEN o.qtybatchs = 0 THEN 1 ELSE round(obl.qtyrequiered / o.qtybatchs, 4) END AS qtybatchsize, round(bomqtyreserved(obl.m_product_id, obl.m_warehouse_id, 0), 4) AS qtyreserved, round(bomqtyavailable(obl.m_product_id, obl.m_warehouse_id, 0), 4) AS qtyavailable, obl.m_warehouse_id, obl.qtybom, obl.isqtypercentage, round(obl.qtybatch, 4) AS qtybatch, obl.m_attributesetinstance_id, l.m_locator_id, l.x, l.y, l.z FROM pp_order_bomline obl JOIN pp_order o ON o.pp_order_id = obl.pp_order_id LEFT JOIN m_storage s ON s.m_product_id = obl.m_product_id AND s.qtyonhand <> 0 AND obl.m_warehouse_id = (( SELECT ld.m_warehouse_id FROM m_locator ld WHERE s.m_locator_id = ld.m_locator_id)) LEFT JOIN m_locator l ON l.m_locator_id = s.m_locator_id ORDER BY obl.m_product_id;
;

CREATE OR REPLACE VIEW rv_pp_order_receipt_issue AS SELECT obl.pp_order_bomline_id, obl.iscritical, p.value, obl.m_product_id, mos.name AS productname, mos.m_attributesetinstance_id, asi.description AS instancename, mos.c_uom_id, u.name AS uomname, obl.qtyrequiered, obl.qtyreserved AS qtyreserved_order, mos.qtyonhand, mos.qtyreserved AS qtyreserved_storage, mos.qtyavailable, mos.m_locator_id, mos.m_warehouse_id, w.name AS warehousename, mos.qtybom, mos.isqtypercentage, mos.qtybatch, obl.componenttype, mos.qtyrequiered - obl.qtydelivered AS qtyopen, obl.pp_order_id FROM rv_pp_order_storage mos JOIN pp_order_bomline obl ON mos.pp_order_bomline_id = obl.pp_order_bomline_id JOIN m_attributesetinstance asi ON mos.m_attributesetinstance_id = asi.m_attributesetinstance_id JOIN c_uom u ON mos.c_uom_id = u.c_uom_id JOIN m_product p ON mos.m_product_id = p.m_product_id JOIN m_warehouse w ON mos.m_warehouse_id = w.m_warehouse_id;
;

CREATE OR REPLACE VIEW rv_pp_order_transactions AS SELECT DISTINCT o.ad_client_id, o.ad_org_id, o.isactive, o.created, o.createdby, o.updatedby, o.updated, o.documentno, ol.m_product_id, mt.m_locator_id, mt.movementdate, o.pp_order_id, o.qtydelivered, o.qtyscrap, ol.qtydelivered AS qtydeliveredline, o.qtydelivered * ol.qtybatch / 100 AS qtyissueshouldbe, ol.qtyscrap AS qtyscrapline, o.qtyscrap * ol.qtybatch / 100 AS qtyissuescrapshouldbe, mt.createdby AS createdbyissue, mt.updatedby AS updatedbyissue, ( SELECT sum(t.movementqty) AS sum FROM m_transaction t WHERE t.pp_order_bomline_id = ol.pp_order_bomline_id) AS qtytodeliver, (o.qtydelivered + o.qtyscrap) * ol.qtybatch / 100 + (( SELECT sum(t.movementqty) AS sum FROM m_transaction t WHERE t.pp_order_bomline_id = ol.pp_order_bomline_id)) AS differenceqty, o.issotrx, o.dateordered FROM pp_order o JOIN pp_order_bomline ol ON ol.pp_order_id = o.pp_order_id LEFT JOIN m_transaction mt ON mt.pp_order_bomline_id = ol.pp_order_bomline_id ORDER BY o.ad_client_id, o.ad_org_id, o.isactive, o.created, o.createdby, o.updatedby, o.updated, o.documentno, ol.m_product_id, mt.m_locator_id, mt.movementdate, o.pp_order_id, o.qtydelivered, o.qtyscrap, ol.qtydelivered, o.qtydelivered * ol.qtybatch / 100, ol.qtyscrap, o.qtyscrap * ol.qtybatch / 100, mt.createdby, mt.updatedby, ( SELECT sum(t.movementqty) AS sum FROM m_transaction t WHERE t.pp_order_bomline_id = ol.pp_order_bomline_id), (o.qtydelivered + o.qtyscrap) * ol.qtybatch / 100 + (( SELECT sum(t.movementqty) AS sum FROM m_transaction t WHERE t.pp_order_bomline_id = ol.pp_order_bomline_id)), o.issotrx, o.dateordered;
;

